package com.sidc.dao.rcu.command.response;

import java.io.Serializable;

/**
 * 
 * @author Allen Huang
 *
 */
public class RcuModelCondition implements Serializable {
	/**
	 * 
	 */
	private static final long serialVersionUID = 2791412987990458576L;
	private boolean isPower;
	private int function;
	private int temperature;
	private int speed;
	private int timer;
	private byte address;
	private int status;
	public RcuModelCondition(boolean isPower, int function, int temperature, int speed, int timer, byte address, int status) {
		// TODO Auto-generated constructor stub
		this.isPower = isPower;
		this.function = function;
		this.temperature = temperature;
		this.speed = speed;
		this.timer = timer;
		this.address = address;
		this.status = status;
	}

	public boolean isPower() {
		return isPower;
	}
	
	public void setPower(boolean isPower) {
		this.isPower = isPower;
	}
	
	public int getFunction() {
		return function;
	}

	public void setFunction(int function) {
		this.function = function;
	}
	
	public int getTemperature() {
		return temperature;
	}

	public void setTemperature(int temperature) {
		this.temperature = temperature;
	}
	
	public int getSpeed() {
		return speed;
	}

	public void setSpeed(int speed) {
		this.speed = speed;
	}
	
	public int getTimer() {
		return timer;
	}

	public void setTimer(int timer) {
		this.timer = timer;
	}
	
	public byte getAddress() {
		return address;
	}

	public void setAddress(byte address) {
		this.address = address;
	}
	
	public int getStatus() {
		return status;
	}

	public void setStatus(int status) {
		this.status = status;
	}
	
	public String toString() {
		
		StringBuilder builder = new StringBuilder();
		builder.append("RcuModelCondition [isPower=\n");
		builder.append(isPower);
		builder.append(", function=\n");
		builder.append(function);
		builder.append(", temperature=\n");
		builder.append(temperature);
		builder.append(", speed=\n");
		builder.append(speed);
		builder.append(", timer=\n");
		builder.append(timer);
		builder.append(", address=\n");
		builder.append(address);
		builder.append(", status=\n");
		builder.append(status);
		builder.append("]");
		return builder.toString();
	}
}
